import React from 'react';
import { motion, useInView, useAnimation } from 'framer-motion';
import About from "../Components/About";
import Goals from "../Components/Goals";
import Herosection from "../Components/Herosection";
import Projects from "../Components/Projects";
import Services from "../Components/Services";

function Homepage() {
  const { ref: aboutRef, inView: aboutInView } = useInView();
  const { ref: goalsRef, inView: goalsInView } = useInView();
  const { ref: projectsRef, inView: projectsInView } = useInView();
  const { ref: servicesRef, inView: servicesInView } = useInView();

  const aboutAnimation = useAnimation();
  const goalsAnimation = useAnimation();
  const projectsAnimation = useAnimation();
  const servicesAnimation = useAnimation();

  const animateInView = (animation, inView) => {
    if (inView) {
      animation.start({
        opacity: 1,
        y: 0,
        transition: { duration: 0.5 },
      });
    }
  };

  return (
    <>
      <Herosection />
      <motion.div ref={servicesRef} initial={{ opacity: 0, y: 50 }} animate={servicesAnimation}>
        <Services />
      </motion.div>
      <motion.div ref={aboutRef} initial={{ opacity: 0, y: 50 }} animate={aboutAnimation}>
        <About />
      </motion.div>
      <motion.div ref={goalsRef} initial={{ opacity: 0, y: 50 }} animate={goalsAnimation}>
        <Goals />
      </motion.div>
      <motion.div ref={projectsRef} initial={{ opacity: 0, y: 50 }} animate={projectsAnimation}>
        <Projects />
      </motion.div>
    </>
  );
}

export default Homepage;
