import React, { useEffect } from 'react';
import { motion, useInView, useAnimation } from 'framer-motion';
import About from "../Components/About";
import Goals from "../Components/Goals";
import Herosection from "../Components/Herosection";
import Projects from "../Components/Projects";
import Services from "../Components/Services";

function Homepage(){
    const heroSectionControls = useAnimation();
    const servicesControls = useAnimation();
    const aboutControls = useAnimation();
    const goalsControls = useAnimation();
    const projectsControls = useAnimation();

    const [heroRef, heroInView] = useInView();
    const [servicesRef, servicesInView] = useInView();
    const [aboutRef, aboutInView] = useInView();
    const [goalsRef, goalsInView] = useInView();
    const [projectsRef, projectsInView] = useInView();

    useEffect(() => {
        if (heroInView) {
            heroSectionControls.start({ opacity: 1, y: 0, transition: { duration: 0.5 } });
        }
    }, [heroInView, heroSectionControls]);

    useEffect(() => {
        if (servicesInView) {
            servicesControls.start({ opacity: 1, y: 0, transition: { duration: 0.5 } });
        }
    }, [servicesInView, servicesControls]);

    useEffect(() => {
        if (aboutInView) {
            aboutControls.start({ opacity: 1, y: 0, transition: { duration: 0.5 } });
        }
    }, [aboutInView, aboutControls]);

    useEffect(() => {
        if (goalsInView) {
            goalsControls.start({ opacity: 1, y: 0, transition: { duration: 0.5 } });
        }
    }, [goalsInView, goalsControls]);

    useEffect(() => {
        if (projectsInView) {
            projectsControls.start({ opacity: 1, y: 0, transition: { duration: 0.5 } });
        }
    }, [projectsInView, projectsControls]);

    return(
        <>
            <Herosection ref={heroRef} animate={heroSectionControls}/>
            <Services ref={servicesRef} animate={servicesControls}/>
            <About ref={aboutRef} animate={aboutControls}/>
            <Goals ref={goalsRef} animate={goalsControls}/>
            <Projects ref={projectsRef} animate={projectsControls}/>
        </>
    )
}

export default Homepage;
